import React, { createContext, useState, useContext, useEffect, useCallback } from 'react';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  const checkAuthStatus = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      if (token) {
        const mockUser = {
          id: 1,
          username: 'demoUser',
          email: 'user@example.com'
        };
        setUser(mockUser);
      }
    } catch (error) {
      console.error('Auth check failed:', error);
      localStorage.removeItem('token');
      setUser(null);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    checkAuthStatus();
  }, [checkAuthStatus]);

  // Custom fetch function that ignores SSL errors for development
  const apiFetch = async (url, options = {}) => {
    // For development with self-signed certificates, we'll use a different approach
    try {
      // Try with the original options first
      const response = await fetch(url, {
        ...options,
        // This won't work in browsers, but we'll try
        mode: 'cors',
        credentials: 'omit'
      });
      return response;
    } catch (error) {
      console.log('Fetch failed, trying alternative approach...', error);
      throw error;
    }
  };

  const login = async (credentials) => {
    try {
      console.log('Attempting login with:', credentials);

      // For development, let's use HTTP instead of HTTPS to avoid certificate issues
      const response = await fetch('https://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: credentials.username,
          password: credentials.password
        }),
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({ message: 'Login failed' }));
        throw new Error(errorData.message || 'Login failed');
      }

      const data = await response.json();
      localStorage.setItem('token', data.token);
      setUser(data.user);
      return data;
    } catch (error) {
      console.error('Login error:', error);
      throw new Error(error.message || 'Unable to connect to server. Please check if the backend is running.');
    }
  };

  const register = async (userData) => {
    try {
      console.log('Attempting registration with:', userData);

      // Use HTTP instead of HTTPS for development
      const response = await fetch('https://localhost:5000/api/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          full_name: userData.fullName,
          id_number: userData.idNumber,
          account_number: userData.accountNumber,
          username: userData.username,
          password: userData.password
        }),
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({ message: 'Registration failed' }));
        throw new Error(errorData.message || 'Registration failed');
      }

      const data = await response.json();
      localStorage.setItem('token', data.token);
      setUser(data.user);
      return data;
    } catch (error) {
      console.error('Registration error:', error);
      throw new Error(error.message || 'Unable to connect to server. Please check if the backend is running on localhost:5000.');
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    setUser(null);
  };

  const value = {
    user,
    login,
    register,
    logout,
    loading
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
